import React, { useState } from 'react';
import axios from 'axios';

function InjectionForm({ onAdd }) {
  const [formData, setFormData] = useState({
    timestamp: new Date().toISOString().substring(0, 16),
    type: '',
    dose: '',
    user_id: ''
  });

  async function handleSubmit(event) {
    event.preventDefault();
    const response = await axios.post('/api/injections', formData);
    onAdd(response.data);
    setFormData({
      timestamp: new Date().toISOString().substring(0, 16),
      type: '',
      dose: '',
      user_id: ''
    });
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Timestamp:
        <input
          type="datetime-local"
          name="timestamp"
          value={formData.timestamp}
          onChange={event =>
            setFormData({ ...formData, timestamp: event.target.value })
          }
          required
        />
      </label>
      <label>
        Type:
        <input
          type="text"
          name="type"
          value={formData.type}
          onChange={event =>
            setFormData({ ...formData, type: event.target.value })
          }
          required
        />
      </label>
      <label>
        Dose:
        <input
          type="number"
          name="dose"
          value={formData.dose}
          onChange={event =>
            setFormData({ ...formData, dose: event.target.value })
          }
          step="0.01"
          min="0"
          max="10"
          required
        />
      </label>
      <label>
        User ID:
        <input
          type="number"
          name="user_id"
          value={formData.user_id}
          onChange={event =>
            setFormData({ ...formData, user_id: event.target.value })
          }
          required
        />
      </label>
      <button type="submit">Add injection</button>
    </form>
  );
}

export default InjectionForm;
